'use strict'
$(document).ready(function() {
var userLanguage=navigator.language||navigator.userLanguage,languageCode=userLanguage.toLowerCase();function loadTranslations(a){$.getJSON("https://sofia.aja.com/src/assets/i18n/"+a+".json").done(function(n){$("[data-i18n]").each(function(){var a=$(this).data("i18n");$(this).text(n[a])})}).fail(function(a,n,e){console.error("Erro ao carregar as traduções:",n,e)})}loadTranslations(languageCode);
});